import{_ as t,p as e,q as i,a1 as d}from"./framework-5866ffd3.js";const a={},r=d('<h2 id="smart-原则" tabindex="-1"><a class="header-anchor" href="#smart-原则" aria-hidden="true">#</a> SMART 原则</h2><ul><li>指标必须是具体的（Specific）</li><li>指标必须是可以衡量的（Measurable）</li><li>指标必须是可以达到的（Attainable）</li><li>指标是要与其他目标具有一定的相关性(Relevant)</li><li>指标必须具有明确的截止期限（Time-bound）</li></ul><table><thead><tr><th>字母</th><th>典型对应</th><th>更多对应</th></tr></thead><tbody><tr><td>S</td><td>Specific（明确）</td><td>Significant（重要）, Stretching（延伸）, Simple（简易）</td></tr><tr><td>M</td><td>Measurable（可衡量）</td><td>Meaningful（有意义）, Motivational（激励）, Manageable（可管理）</td></tr><tr><td>A</td><td>Achievable（可达成）</td><td>Appropriate（适宜）, Attainable（可达成）, Agreed（同意）, <br>Assignable（可分配）,Actionable（可行动）,Action-oriented（行动导向）, <br> Ambitious（雄心）</td></tr><tr><td>R</td><td>Relevant（相关）</td><td>Realistic（实际）, Results/Results-focused/Results-oriented, Resourced（资源）, <br> Rewarding（奖励）</td></tr><tr><td>T</td><td>Time-bound （时限）</td><td>Time-oriented（时间定向）, Time framed（时限）, Timed（时限）,<br> Time-based（基于时间）, Timeboxed（时限）, Timely（及时）, <br> Time-Specific（明确时间）, Timetabled（表定时间）, Time limited（时限）, <br>Trackable（可跟踪）, Tangible（明白）</td></tr></tbody></table><h3 id="swot-分析法" tabindex="-1"><a class="header-anchor" href="#swot-分析法" aria-hidden="true">#</a> SWOT 分析法</h3>',4),l=[r];function n(o,s){return e(),i("div",null,l)}const h=t(a,[["render",n],["__file","method.html.vue"]]);export{h as default};
